css:
@media print {
    .page-break {
        page-break-after: always;
    }
}

.clinic-print{
    border: 1px solid #000;
}

.head{
    border-bottom: 1px solid #000;
    padding: 6px;
}

.head h2{
    font-size: 30px;
    font-weight: 700;
    text-transform: uppercase;
    margin: 0;
    padding: 0;
}

.head h5{
    font-size: 22px;
    font-weight: 500;
    margin: 0;
    padding: 0;
}

.body{
    margin: 0px 10px;
    border-bottom: 2px dashed #000;
}

.print-format td, .print-format th{
    padding: 10px 0 !important;
    margin: 0;
}

.f-tr td:first-child{
    text-align: left !important;
    text-transform: uppercase;
}

tr td{
    font-size: 20px;
    font-weight: 500;
}

.f-tr td {
    text-align: center;
    font-size: 20px;
    font-weight: 500;
}

.f-tr td:last-child{
    text-align: right !important;

}

.sec-tr td{
    font-size: 28px;
}

.foot{
    padding: 10px;
}

.foot h5{
    margin: 0;
    padding: 0;
    font-size: 20px;
}

.date{
    display: flex;
    align-items: center;
    justify-content: space-between;
}
